import React, {useEffect, useState} from "react";
import './pages/pages.css';
import './components/components.css';
import {Routes, Route, BrowserRouter} from "react-router-dom";
import PrivateRoute from "./routes/PrivateRoute";
import PublicRoute from "./routes/PublicRoute";
import Home from './pages/Home';
import Login from './pages/Login';
import { Navigate } from "react-router-dom";
import Signup from "./pages/Signup";

 async function login(email, password) {
    const response = await fetch('http://localhost:8080/users/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      credentials: 'include',
      body: JSON.stringify({email, password}),
    });

    if(!response.ok)
    {
      throw new Error('invalid email or password')
    }

    const user = await response.json();
    return user;
  }

  async function fetchCurrentUser(){
    const response = await fetch('http://localhost:8080/users/session', {credentials: 'include'});

    if(!response.ok)
      return null;

    return null;
  }

  async function logout(){
    await fetch('http://localhost:8080/users/logout',{
      method: 'POST',
      credentials: 'include',
    })
  }

function App() {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);

  useEffect(()=>{
    fetchCurrentUser().then(setUser).catch(()=>setUser(null));
  }, []);

  async function handleLogin(email, password)
  {
    try{
      const loggedUser = await login(email, password);
      setUser(loggedUser);
      setError(null);
    }catch(e){
      setError(e.message);
    }

    async function handleLogout(){
      await logout();
      setUser(null);
    }
  }

  return(
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element= {<Navigate to="/home" />}
        />
        <Route
          path="/login"
          element= {<PublicRoute user={user}> <Login/> </PublicRoute>}
        />
        <Route
          path="/signup"
          element= {<PublicRoute user={user}> <Signup/> </PublicRoute>}
        />
        <Route
          path="/home"
          element= {<Home/>}
        />

        <Route
          path="/profile"
          element= {<PrivateRoute user={user}> <Profile/> </PrivateRoute>}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
